    function edit($chauffeur_id)
    {   
        // check if the chauffeurs exists before trying to edit it
        $data['chauffeurs'] = $this->Chauffeurs_model->get_chauffeurs($chauffeur_id);
        
        if(isset($data['chauffeurs']['chauffeur_id']))
        {
