        $chauffeurs = $this->Chauffeurs_model->get_chauffeurs($chauffeur_id);

        // check if the chauffeurs exists before trying to delete it
        if(isset($chauffeurs['chauffeur_id']))
        {
            $this->Chauffeurs_model->delete_chauffeurs($chauffeur_id);
